SELECT job.JOB_TITLE AS Cargo, ROUND(AVG(emp.SALARY), 2) AS 'Média salarial', CASE WHEN ROUND(AVG(emp.SALARY), 2) <= 5800 THEN 'Júnior' WHEN ROUND(AVG(emp.SALARY), 2) <= 7500 THEN 'Pleno' WHEN ROUND(AVG(emp.SALARY), 2) <= 10500 THEN 'Sênior' WHEN ROUND(AVG(emp.SALARY), 2) > 10500 THEN 'CEO' END AS Senioridade FROM hr.jobs AS job INNER JOIN hr.employees AS emp ON job.JOB_ID = emp.JOB_ID GROUP BY emp.JOB_ID ORDER BY ROUND(AVG(emp.SALARY), 2);